package Pattern_program;

 public class Number_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1; i<=5;i++)
		{
			//second for loop to iterate columns
			for(int j=1;j<=i;j++)
			{
				System.out.print(j + "  " );
			}
			System.out.println();
		}

	}

}
